import React from 'react';
import UserStore from './stores/UserStore'
import './App.css';
import LoginIcon from "./imgs/login-icon.png"
import LogIn from "./components/login"
import SignUp from "./components/signup"
import axios from 'axios';

export default class App extends React.Component {
  constructor() {
    super()
    this.state = {
      isLogIn: true,
      isSignUp: false,
      email: "",
      username: "",
      password: ""
    }
  }

  selectSignUp = () => {
    this.setState({
      isLogIn: false,
      isSignUp: true})
  }

  selectLogIn = () => {
    this.setState({
      isLogIn: true,
      isSignUp: false})
  }

  handleChange = (e) => {
    e.preventDefault()
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handlePost = (e) => {
    e.preventDefault()
    let { email, password } = this.state
    let req = {
      email,
      password
    }
    axios.post('http://localhost:4000/login', req)
          .then(res => {
            const temp = res.data.message;
            // alert.(temp);
            console.log(temp)
          })
          .catch(err => {
            console.log(err)
          })
  }

  async  componentDidMount() {
    try {
      let res = await fetch('/login', {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-type': 'Application/json',
        }
      });
      let result = await res.json();
      if (result && result.success) {
        UserStore.loading = false
        UserStore.isLoggedIn = true
        UserStore.username = result.username
      }
      else {
        UserStore.loading = false
        UserStore.isLoggedIn = false
      }
    }
    catch(e) {
      UserStore.loading = false
      UserStore.isLoggedIn = false
    }
  }

  async  doLogout() {
    try {
      let res = await fetch('/logout', {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-type': 'Application/json',
        }
      });
      let result = await res.json();
      if (result && result.success) {
        UserStore.isLoggedIn = false
        UserStore.username = ''
      }
    }
    catch(e) {
      console.log(e)
    }
  }

  render() {
    if(UserStore.loading) {
      return (
        <div className="row">
          Loading... please wait
        </div>
      )
    }
    else {
      if(UserStore.isLoggedIn) {
        return (
          <div className="row">
            Welcome {UserStore.username}
            <button>Logout Button</button>
          </div>
        )
      }

    return (
      <div className="wrapper row">
        <div className="left-side col">
          <img src={LoginIcon} alt="user icon" />
          <div className="welcome">Adios !</div>
        </div>
        <div className="right-side col">
          <div className="navbar row">
            <div onClick={this.selectLogIn} className={this.state.isLogIn ? "btn-selected" : "btn-not-selected"}>
              LOG IN
            </div>
            <div onClick={this.selectSignUp} className={this.state.isSignUp ? "btn-selected" : "btn-not-selected"}>
              SIGN UP
            </div>
          </div>
          {
            this.state.isLogIn 
                              ? <LogIn onChange={this.handleChange} values={this.state}  onPost={(e) => this.handlePost(e)}/> 
                              : <SignUp onChange={this.handleChange} values={this.state} onPost={(e) => this.handlePost(e)} />
          }
        </div>
      </div>
    );
  }
}
}
