import React from 'react';
import './App.css';
import LoginIcon from "./imgs/login-icon.png"
import LogIn from "./components/login"
import SignUp from "./components/signup"

export default class App extends React.Component {
  constructor() {
    super()
    this.state = {
      isLogIn: true,
      isSignUp: false,
      email: "",
      username: "",
      password: ""
    }
  }

  selectSignUp = () => {
    this.setState({
      isLogIn: false,
      isSignUp: true})
  }

  selectLogIn = () => {
    this.setState({
      isLogIn: true,
      isSignUp: false})
  }

  handleChange = (e) => {
    e.preventDefault()
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    return (
      <div className="wrapper row">
      <div className="form-wrapper col">
          <form action="/" method="POST">
              <input onChange={e => this.props.onChange(e)} type="email" name="email"  placeholder="Email..." />
              <input onChange={e => this.props.onChange(e)} type="password" name="password"  placeholder="Password..." />
              <input type="submit" value="Submit" />
          </form>
      </div>
        <div className="right-side col">
          <div className="navbar row">
            <div onClick={this.selectLogIn} className={"nav-items " + (this.state.isLogIn ? "btn-selected" : " ")}>
              LOG IN
            </div>
            <div onClick={this.selectSignUp} className={"nav-items " + (this.state.isSignUp ? "btn-selected" : " ")}>
              SIGN UP
            </div>
          </div>
          {
            this.state.isLogIn 
                              ? <LogIn onChange={this.handleChange} values={this.state} /> 
                              : <SignUp onChange={this.handleChange} values={this.state} />
          }
        </div>
      </div>
    );
  }
}
