import React from 'react';
import './App.css';
import UserStore from './stores/UserStore'
import LoginIcon from "./imgs/login-icon.png"
import LogIn from "./components/login"
import SignUp from "./components/signup"

export default class App extends React.Component {
    render() {
        return (
            <div className="wrapper row">
              <div className="left-side col">
                <img src={LoginIcon} alt="user icon" />
                <div className="welcome">Adios !</div>
              </div>
              <div className="right-side col">
                <div className="navbar row">
                  <div onClick={this.props.selectLogIn} className={this.props.state.isLogIn ? "btn-selected" : "btn-not-selected"}>
                    LOG IN
                  </div>
                  <div onClick={this.props.selectSignUp} className={this.props.state.isSignUp ? "btn-selected" : "btn-not-selected"}>
                    SIGN UP
                  </div>
                </div>
                {
                  this.state.isLogIn 
                                    ? <LogIn onChange={this.props.handleChange} values={this.props.state}  /> 
                                    : <SignUp onChange={this.props.handleChange} values={this.props.state}  />
                }
              </div>
            </div>
        )
    }
}
