import React from 'react';
import Login from './components/login'
import './App.css';
import LoginIcon from "./imgs/login-icon.png"

export default class App extends React.Component {
  constructor() {
    super()
    this.state = {
      isLogin: true,
      isSignin: false
    }
  }

  toggleSelected = () => {
    this.setState(prev => {
      return {
      isLogin: !prev.isLogin,
      isSignin: !prev.isSignin}
    })
  }

  render() {
    return (
      <div className="app">
        <div className="form row">
          <div className="wallpaper col">
            <img src={LoginIcon} alt="login-icon" />
            <div className="welcome-message">
              Welcome ! <br /> Login or Signup
            </div>
          </div>

          <div className="form-fields">

            <div className="nav row">
              <div onClick={this.toggleSelected} className={"nav-elem " + (this.state.isLogin ? "btn-selected" : " ")}>
                LOG IN
              </div>
              <div onClick={this.toggleSelected} className={"nav-elem " + (this.state.isSignin ? "btn-selected" : " ")}>
                SIGN IN
              </div>
            </div>

            <Login />

          </div>

        </div>
      </div>
    );
  }
}
